// C++ program to calculate average of array elements
#include <iostream>
using namespace std;

// Function that return average of an array.
double average(int a[], int n)
{
    // Find sum of array element
    int sum = 0;
    for (int i=0; i<n; i++)
       sum += a[i];
    return sum/n;
}

